mapper.update:
update thain_job_execution
set logs = #{logs}
where id = #{jobExecutionId}

update thain_job_execution
set status = #{status}
where id = #{jobExecutionId}

update thain_job_execution
set create_time = now()
where id = #{jobExecutionId}

mapper.delete:
delete
from thain_job_execution
where id in (

    #{id}

)

delete
from thain_job_execution
where flow_execution_id in (

    #{id}

)

mapper.select:
select id
from thain_job_execution
where flow_execution_id not in (

    #{id}

) and update_time < from_unixtime(unix_timestamp(now()) - 60 * 60)
